import { POST_FAIL, POST_ONE_FAIL, POST_ONE_REQUEST, POST_ONE_SUCCESS, POST_REQUEST, POST_SUCCESS } from "../constants/postConstants";

export const postListReducer = (state = { posts: [] }, action) => {
    switch (action.type) {
        case POST_REQUEST:
            return { loading: true };
        case POST_SUCCESS:
            return { loading: false, posts: action.payload };
        case POST_FAIL:
            return { loading: false, error: action.payload };
        default:
            return state;
    }
}

export const postReducer = (state = { post: {} }, action) => {
    switch (action.type) {
        case POST_ONE_REQUEST:
            return { loading: true };
        case POST_ONE_SUCCESS:
            return { loading: false, foundPost: action.payload };
        case POST_ONE_FAIL:
            return { loading: false, error: action.payload };
        default:
            return state;
    }
}
