import { POST_FAIL, POST_REQUEST, POST_SUCCESS } from "../constants/postConstants";

export const postReducer = (state = {}, action) => {
    switch (action.type) {
        case POST_REQUEST:
            return { loading: true };
        case POST_SUCCESS:
            return { loading: false, userInfo: action.payload };
        case POST_FAIL:
            return { loading: false, error: action.payload };
        default:
            return state;
    }
}
