import React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { getPost } from '../actions/postActions';

export default function PostBox(props) {
    const { post } = props;

    const onePost = useSelector((state) => state.onePost);
    const { loading, error } = onePost;

    const dispatch = useDispatch();
    const submitHandler = (e) => {
        e.preventDefault();
        dispatch(getPost(post._id));
        // if (!loading && !error) {
        //     window.location.replace(`http://localhost:3001/editEntry/${post._id}`);
        // }
    }
    return (
        <div key={post._id} className="margin">
            <a href="editEntry/${post._id}" onClick={submitHandler}>
                <div className="postbox">
                    <img className="entryGraphic" src="../images/writing-icon.jpg" alt="Writing Graphic"></img>
                    <div className="previewText">
                        <h1>{post.title}</h1>
                        <p>Date: {post.timestamp.slice(0, post.timestamp.indexOf("T"))}</p>
                        <p>Time: {post.timestamp.slice(post.timestamp.indexOf("T") + 1, post.timestamp.indexOf("."))}</p>
                    </div>
                </div>
            </a>
        </div>
    )
};
